def on_on_overlap(sprite, otherSprite):
    global snails_kill
    if otherSprite.y - 5 > sprite.y + 5:
        otherSprite.destroy()
        snails_kill += 1
    else:
        woodcutter.start_effect(effects.fountain, 500)
        statusbar2.value += -1
        info.change_life_by(-1)
        pause(500)
sprites.on_overlap(SpriteKind.player, SpriteKind.subordinate, on_on_overlap)

